import React from "react";
import { Todo } from "../model/todoModel";
import { AiOutlineClose, AiOutlineCheck } from "react-icons/ai";
import {MdDone} from "react-icons/md";
import axios from 'axios'

interface Props {
  todo: Todo;
  todos: Todo[];
  setTodos: React.Dispatch<React.SetStateAction<Todo[]>>;
}

const TodoItem: React.FC<Props> = ({ todo, setTodos, todos }) => {

  const handleDelete = (id: number) => {
      axios.delete<Todo[]>(`http://localhost:3001/todos/${id}`).then((res) => {
        console.log('delete successful');
      });
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  const handleDone = (id: Number) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, isDone: !todo.isDone } : todo
      )
    );
  };

  return (
    <div className="todoItem">
      {todo.isDone ? (
        <s>{todo.name}</s>
      ) : (
        <>
          <AiOutlineCheck style={{background:"green", }} />
          <span onClick={() => handleDone(todo.id)}>{todo.name}</span>
        </>
      )}
      <span className="icon">
        <AiOutlineClose onClick={() => handleDelete(todo.id)} />
      </span>
    </div>
  );
};

export default TodoItem;
